import './App.css';
import React, { useState, useEffect } from "react";
import Banner from './components/banner.js';
import Product from './components/product.js';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/products")
      .then((res) => res.json())
      .then((data) => {
        if (data.results) {
          setProducts(data.results);
        } else {
          console.error('Error fetching products:', data);
        }
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  }, []);

  // Define your styles object
  const productStyle = {
    padding: '0.5rem', // 2 * 4px = 8px
    paddingLeft: '1.5rem', // 6 * 4px = 24px
    paddingRight: '1.5rem', // 6 * 4px = 24px
    paddingBottom: '2rem', // 8 * 4px = 32px
    paddingTop: '2rem', // 8 * 4px = 32px
    backgroundColor: '#e5e7eb', // This is a light gray color
    margin: '0.5rem', // 2 * 4px = 8px
    width: '10rem' // This is an approximation, Tailwind's width scale is not linear
  };

  return (
    <div className="App">
      <Banner/>
      <h1>ðŸ”¥HOT DEALSðŸ”¥</h1>
      <ul>
        {products.map((product) => (
          // Apply the style object to your Product component
          <Product key={product.Product_ID} product={product} style={productStyle} />
        ))}
      </ul>
    </div>
  );
}

export default App;
