import React, { useState, useEffect } from "react";
import Banner from './components/banner.js';
import Product from './components/product.js';

function App() {
  const [products, setProducts] = useState([]);
  const [savedProducts, setSavedProducts] = useState([]); // State for saved products

  useEffect(() => {
    fetch("http://localhost:8000/products")
      .then((res) => res.json())
      .then((data) => {
        if (data.results) {
          setProducts(data.results);
        } else {
          console.error('Error fetching products:', data);
        }
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  }, []);

  // Function to handle saving a product for later
  const saveForLater = (product) => {
    setSavedProducts((prevSavedProducts) => {
      // Check if the product is already saved to prevent duplicates
      if (!prevSavedProducts.find(p => p.Product_ID === product.Product_ID)) {
        return [...prevSavedProducts, product];
      }
      return prevSavedProducts;
    });
  };

  const productStyle = {
    padding: '0.5rem',
    paddingLeft: '1.5rem',
    paddingRight: '1.5rem',
    paddingBottom: '2rem',
    paddingTop: '2rem',
    backgroundColor: '#e5e7eb',
    margin: '0.5rem',
    width: '16rem',
    listStyleType: 'none',
    textAlign: 'center'
  };

  return (
    <div className="App">
      <Banner/>
      <h1 style={{ textAlign: 'center' }}>ðŸ”¥HOT DEALSðŸ”¥</h1>
      <ul style={{
        listStyleType: 'none',
        display: 'flex',
        justifyContent: 'center',
        flexWrap: 'wrap',
        padding: 0
      }}>
        {products.map((product) => (
          <Product key={product.Product_ID} product={product} style={productStyle} saveForLater={saveForLater} />
        ))}
      </ul>
      {savedProducts.length > 0 && (
        <>
          <h2 style={{ textAlign: 'center' }}>Saved for Later</h2>
          <ul style={{
            listStyleType: 'none',
            display: 'flex',
            justifyContent: 'center',
            flexWrap: 'wrap',
            padding: 0
          }}>
            {savedProducts.map((savedProduct) => (
              <li key={savedProduct.Product_ID} style={productStyle}>
                {savedProduct.Product_Name}
                {/* Here you could also add an option to remove from saved or to add back to cart */}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
