import './App.css';
import React, { useState, useEffect } from "react";
import Banner from './components/banner.js';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/products")
      .then((res) => res.json())
      .then((data) => {
        if (data.items) { // Assuming the key in your JSON for products array is 'items'
          setProducts(data.items);
        } else {
          console.error('Error fetching products:', data);
        }
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  }, []);

  return (
    <div className="App">
      <Banner/>
      <h1>ðŸ”¥HOT DEALSðŸ”¥</h1>
      <div>
        {products.map((product, index) => (
          <div key={index}> {/* Use a div here instead of p for containing elements */}
            <h3>{product.name}</h3>
            <p>Sale Price: {product.salePrice}</p> {/* Adjusted to match your JSON structure */}
            <p>Original Price: {product.originalPrice}</p> {/* Adjusted to match your JSON structure */}
            <a href={product.url} target="_blank" rel="noopener noreferrer"> {/* Wrapping image in anchor tag for redirection */}
              <img src={product.image} alt={product.name} style={{ width: '100px', height: 'auto' }}/>
            </a>
          </div> 
        ))}
      </div>

    </div>
  );
}

export default App;
